mapper.select:
SELECT  COALESCE(MAX(REGI_SNO),0) + 1 AS MAX_NO
  FROM  aca1100
 WHERE  A.cif      = #{cif}
   AND  A.ctdt_dcd = #{ctdt_dcd}
   AND  A.use_yn = 'Y'

mapper.insert:
INSERT 
  INTO ACA1100
(  
   cif
,  ctdt_dcd
,  regi_sno
,  post_no_sys_dcd
,  post_no
,  post_no_adr
,  enc_post_no_ptcl_adr
,  tel_area_no
,  tel_ptcl_area_no
,  enc_tel_nd_no
,  mail
,  use_yn

,  opr_dttm
,  opr_id
,  opr_trd_id
,  opr_tmn_id
)
VALUES
(
   #{cif}                        /*고객식별번호*/
,  #{ctdt_dcd}
,  #{regi_sno}
,  #{post_no_sys_dcd}
,  #{post_no}
,  #{post_no_adr}
,  #{enc_post_no_ptcl_adr}
,  #{tel_area_no}
,  #{tel_ptcl_area_no}
,  #{enc_tel_nd_no}
,  #{mail}
,  'Y'

,  NOW()                         /*조작일시*/
,  #{opr_id}                     /*조작자ID*/
,  #{opr_trd_id}                 /*조작거래ID*/
,  #{opr_tmn_id}                 /*조작터미널ID*/
)

mapper.update:
UPDATE  ACA1100
   SET  
        use_yn       = 'N'
     ,  opr_dttm     = NOW()
     ,  opr_id       = #{opr_id}
     ,  opr_trd_id   = #{opr_trd_id}
     ,  opr_tmn_id   = #{opr_tmn_id}
 WHERE  A.cif      = #{cif}
   AND  A.ctdt_dcd = #{ctdt_dcd}
   AND  A.use_yn   = 'Y'
